INSERT INTO "public"."companies" ("id", "name", "description", "logo_url", "website_url", "social_media_id", "last_scraped_at", "scrape_frequency", "category_id", "created_at", "updated_at", "founding_year", "is_government") OVERRIDING SYSTEM VALUE VALUES 
	(1, 'Agnikul Cosmos', 'Agnikul Cosmos is a space tech company building small satellite launch vehicles. We are building a full stack launch vehicle to take small satellites to space.', 'agnikul-logo.jpg', 'https://agnikul.in/#/', 16, NULL, 'bi_weekly', 16, NULL, NULL, 2017, false),
	(2, 'NASA', 'The National Aeronautics and Space Administration is an independent agency of the U.S. federal government responsible for the civilian space program, as well as aeronautics and aerospace research.', 'nasa-logo.jpg', 'https://www.nasa.gov/', 2, NULL, 'twice_daily', 16, NULL, NULL, 1958, true),
	(3, 'ISRO', 'The Indian Space Research Organisation is the space agency of the Government of India and the main body in India to perform space research and exploration.', 'isro-logo.jpg', 'https://www.isro.gov.in/', 3, NULL, 'daily', 16, NULL, NULL, 1969, true),
	(4, 'ESA', 'The European Space Agency is an intergovernmental organisation dedicated to the exploration of space. It was established in 1975 and headquartered in Paris, France.', 'esa-logo.jpg', 'https://www.esa.int/', 4, NULL, 'twice_daily', 16, NULL, NULL, 1975, true),
	(5, 'CSA', 'The Canadian Space Agency is the national space agency of Canada, established in 1989. The agency is responsible for coordinating all civil, space-related policies and programs on behalf of the Government of Canada.', 'csa-logo.jpg', 'https://www.asc-csa.gc.ca/eng/', 5, NULL, 'twice_daily', 16, NULL, NULL, 1989, true),
